package oopEx;

public class MathExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Math 클래스 : 수학에 관련된 기능을 정의한 클래스
		// 모든 메서드는 static 으로 선언되어서, 필요시에 class.method()로 사용합니다. 
		// 일반적인 메서드 몇가지만 보고 skip..
		
		System.out.println(Math.ceil(2.9));// 소수점이하 올려서 정수부만 double로 리턴
		System.out.println(Math.floor(2.9));// 소수점 이하 내림..
		System.out.println(Math.round(2.9));// 반올림..
		
		System.out.println();
	}

}
